// CustomerListItem.js
import React, { useState } from 'react';
import './CustomerListItem.css';

const CustomerListItem = ({ customer }) => {
    const [isExpanded, setIsExpanded] = useState(false);

    return (
        <div className={`customer-item ${isExpanded ? 'expanded' : ''}`} onClick={() => setIsExpanded(!isExpanded)}>
            <h2>{customer.FirstName} {customer.LastName}</h2>
            {isExpanded && <div className="customer-details">
                <p>Email: {customer.Email}</p>
                <p>Phone: {customer.Phone}</p>
                <p>Address: {customer.StreetAddress}, {customer.City}, {customer.State}, {customer.ZipCode}</p>
                {/* Display more customer details here */}
            </div>}
        </div>
    );
}

export default CustomerListItem;
