// ./frontend/src/components/Customers/Header/AddCustomerForm.js

import React, { useState } from 'react';
import './AddCustomerForm.css';


const AddCustomerForm = ({ onClose }) => {
    const [formData, setFormData] = useState({
        StreetAddress: '',
        City: '',
        State: '',
        ZipCode: '',
        GeoCoordinates: '',
        FirstName: '',
        LastName: '',
        Email: '',
        Phone: ''
    });

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Here you can make the API call to submit the form data
        // After submission, close the form
        onClose();
    };

    return (
        <div className="add-customer-form-overlay">
            <form onSubmit={handleSubmit} className="add-customer-form">
                {/* Add the rest of the input fields similarly */}
                <input
                    type="text"
                    name="FirstName"
                    placeholder="First Name"
                    value={formData.FirstName}
                    onChange={handleInputChange}
                />
                <input
                    type="text"
                    name="LastName"
                    placeholder="Last Name"
                    value={formData.LastName}
                    onChange={handleInputChange}
                />
                {/* ... */}
                <button type="submit">Submit</button>
                <button onClick={onClose}>Close</button>
            </form>
        </div>
    );
};

export default AddCustomerForm;
