// ./frontend/src/api/customerAPI.js

export const getCustomerAddressesByClientID = (clientID) => {
    console.log(`Fetching customer addresses for clientID: ${clientID}`);  // Log the clientID being passed in

    return fetch(`/api/customers/address/${clientID}`)
        .then(response => {
            if (!response.ok) {
                console.error('Error response:', response);  // Log any non-OK responses for debugging
                throw new Error('Failed to fetch customer addresses');
            }
            return response.json();
        })
        .then(data => {
            console.log('Received data:', data);  // Log the data received from the server
            return data;
        });
}
