// ./frontend/src/api/customerAPI.js

export const getCustomerAddressesByClientID = (clientID) => {
    return fetch(`/api/customers/address/${clientID}`)
        .then(response => {
            if (!response.ok) {
                throw new Error('Failed to fetch customer addresses');
            }
            return response.json();
        });
}
