import React from 'react';
import './SortDropdown.css';

const SortDropdown = () => {
    return (
        <select className="sort-dropdown">
            {/* Example sort options */}
            <option value="nameAsc">Name (A-Z)</option>
            <option value="nameDesc">Name (Z-A)</option>
        </select>
    );
}

export default SortDropdown;
