import React, { useState } from 'react';
import './CustomerListItem.css';

const CustomerListItem = ({ customer }) => {
    const [isExpanded, setIsExpanded] = useState(false);

    return (
        <div className={`customer-item ${isExpanded ? 'expanded' : ''}`} onClick={() => setIsExpanded(!isExpanded)}>
            <h2>{customer.name}</h2>
            {isExpanded && <div className="customer-details">
                {/* Display more customer details here */}
            </div>}
        </div>
    );
}

export default CustomerListItem;
