// ./frontend/src/components/Customers/ListView/ListView.js

import React from 'react';
import CustomerListItem from './CustomerListItem';
import './ListView.css';

const ListView = ({ customers }) => {
    return (
        <div className="customer-list">
            {customers.map(customer => (
                <CustomerListItem 
                    key={customer.CustomerID} 
                    customer={customer} 
                    isExpanded={customer.CustomerID === expandedCustomerID} 
                    onToggle={() => setExpandedCustomerID(prev => prev === customer.CustomerID ? null : customer.CustomerID)}
                />
            ))}
        </div>
    );
}

export default ListView;
