import React from 'react';
import './DetailsPanel.css';
import RelatedJobsButton from './RelatedJobsButton';
import EditInfoButton from './EditInfoButton';
import EditCustomerForm from './EditCustomerForm';


const DetailsPanel = ({ customer, editCustomerData, onEditClick, setShowEditForm }) => {

    return (
        <div className="details-panel">
            <p>Email: {customer.email}</p>
            <p>Phone: {customer.phone}</p>
            <p>Address: {customer.address}</p>
            <RelatedJobsButton customerId={customer.id} />
            <EditInfoButton customerId={customer.id} onClick={() => onEditClick(customer)} />
            {setShowEditForm && <EditCustomerForm customer={editCustomerData} onClose={() => setShowEditForm(false)} />}

        </div>
    );
}

export default DetailsPanel;
